// SPDX-License-Identifier: MIT
pragma solidity =0.8.28;
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Enum} from "./Enum.sol";

interface GnosisSafe {
    function execTransactionFromModule(
        address to,
        uint256 value,
        bytes calldata data,
        Enum.Operation operation
    ) external returns (bool success);

    function isOwner(address owner) external view returns (bool);

    function getThreshold() external view returns (uint256);
}

contract PermissionModule {
    string public constant NAME = "Permission Module";
    string public constant VERSION = "0.1.1";
    enum Role {
        None,
        Trader,
        Approver,
        Owner
    }
    enum TxStatus {
        Proposed,
        Approved,
        Executed,
        Cancelled
    }

    struct Transaction {
        address[] recipients;
        uint256[] values;
        bytes data;
        Enum.Operation operation;
        TxStatus status;
        uint256 approvals;
    }

    mapping(address => mapping(address => Role)) public roles;
    mapping(address => mapping(bytes32 => Transaction)) public transactions;

    event TransactionProposed(
        address indexed safe,
        bytes32 indexed txHash,
        address indexed proposer
    );
    event TransactionApproved(
        address indexed safe,
        bytes32 indexed txHash,
        address indexed approver,
        uint256 approvals
    );
    event TransactionExecuted(
        address indexed safe,
        bytes32 indexed txHash,
        address executor
    );
    event TransactionCancelled(
        address indexed safe,
        bytes32 indexed txHash,
        address canceller
    );

    modifier onlyTrader(address safe) {
        require(
            GnosisSafe(safe).isOwner(msg.sender) ||
                roles[safe][msg.sender] == Role.Trader,
            "Not authorized: Only Owner"
        );
        _;
    }

    modifier onlyApprover(address safe) {
        require(
            GnosisSafe(safe).isOwner(msg.sender) ||
                roles[safe][msg.sender] == Role.Approver,
            "Not authorized: Only Owner"
        );
        _;
    }

    modifier onlyOwner(address safe) {
        require(
            GnosisSafe(safe).isOwner(msg.sender),
            "Not authorized: Only Owner"
        );
        _;
    }

    function propose(
        address safe,
        address[] calldata recipients,
        uint256[] calldata values,
        bytes calldata data,
        Enum.Operation operation
    ) external onlyTrader(safe) returns (bytes32) {
        require(
            recipients.length == values.length,
            "Recipients and values length mismatch"
        );

        bytes32 txHash = keccak256(
            abi.encode(safe, recipients, values, data, operation)
        );
        require(
            transactions[safe][txHash].status == TxStatus.Proposed,
            "Transaction already exists"
        );

        transactions[safe][txHash] = Transaction({
            recipients: recipients,
            values: values,
            data: data,
            operation: operation,
            status: TxStatus.Proposed,
            approvals: 0
        });

        emit TransactionProposed(safe, txHash, msg.sender);
        return txHash;
    }

    function approve(address safe, bytes32 txHash) external onlyApprover(safe) {
        Transaction storage txn = transactions[safe][txHash];
        require(
            txn.status == TxStatus.Proposed,
            "Transaction not proposed or already approved"
        );

        txn.approvals += 1;
        emit TransactionApproved(safe, txHash, msg.sender, txn.approvals);

        if (txn.approvals >= GnosisSafe(safe).getThreshold()) {
            txn.status = TxStatus.Approved;
        }
    }

    function execute(address safe, bytes32 txHash) external onlyOwner(safe) {
        Transaction storage txn = transactions[safe][txHash];
        require(txn.status == TxStatus.Approved, "Transaction not approved");
        require(
            txn.approvals >= GnosisSafe(safe).getThreshold(),
            "Insufficient approvals"
        );

        txn.status = TxStatus.Executed;
        emit TransactionExecuted(safe, txHash, msg.sender);

        for (uint256 i = 0; i < txn.recipients.length; i++) {
            require(
                GnosisSafe(safe).execTransactionFromModule(
                    txn.recipients[i],
                    txn.values[i],
                    txn.data,
                    txn.operation
                ),
                "Transaction execution failed"
            );
        }
    }

    function cancel(address safe, bytes32 txHash) external onlyOwner(safe) {
        Transaction storage txn = transactions[safe][txHash];
        require(
            txn.status == TxStatus.Proposed,
            "Transaction not proposed or already processed"
        );

        txn.status = TxStatus.Cancelled;
        emit TransactionCancelled(safe, txHash, msg.sender); // Corrected argument order
    }

    //geter, setter, checker

    function getRole(address safe, address user) external view returns (Role) {
        return roles[safe][user];
    }

    function setRole(address user, Role role) external {
        roles[msg.sender][user] = role;
    }

    function getTransaction(
        address safe,
        bytes32 txHash
    )
        external
        view
        returns (
            address[] memory recipients,
            uint256[] memory values,
            bytes memory data,
            Enum.Operation operation,
            TxStatus status,
            uint256 approvals
        )
    {
        Transaction memory txn = transactions[safe][txHash];
        return (
            txn.recipients,
            txn.values,
            txn.data,
            txn.operation,
            txn.status,
            txn.approvals
        );
    }

    function getTransactionStatus(
        address safe,
        bytes32 txHash
    ) external view returns (TxStatus) {
        return transactions[safe][txHash].status;
    }

    function getTransactionApprovals(
        address safe,
        bytes32 txHash
    ) external view returns (uint256) {
        return transactions[safe][txHash].approvals;
    }

    function isTransactionApproved(
        address safe,
        bytes32 txHash
    ) external view returns (bool) {
        return
            transactions[safe][txHash].approvals >=
            GnosisSafe(safe).getThreshold();
    }
}
